+    cpp << "#include <" << stem << ".capnp.proxy-impl.h>\n";
+    cpp << "#include <" << PROXY_IMPL << ">\n\n";
+    cpp << "namespace interfaces {\n";
+    cpp << "namespace capnp {\n";
+
+    std::string guard = stem;
