+    impl << "#include <" << stem << ".capnp.proxy.h>\n";
+    impl << "#include <" << stem << "-impl.h>\n\n";
+    impl << "namespace interfaces {\n";
+    impl << "namespace capnp {\n";
+
+    std::ofstream h(stem + ".capnp.proxy.h");
