+    CleanupIt addCleanup(std::function<void()> fn)
+    {
+        return m_cleanup_fns.emplace(m_cleanup_fns.begin(), std::move(fn));
+    }
+
+    void removeCleanup(CleanupIt it) { m_cleanup_fns.erase(it); }
