+{
+    if (!input.has()) {
+        value.reset();
+        return;
+    }
+    if (value) {
+        ReadFieldUpdate(TypeList<LocalType>(), invoke_context, input, *value);
+    } else {
+        ReadField(TypeList<LocalType>(), invoke_context, input, Emplace<DestValue>(value));
