+template <typename LocalType, typename Input, typename DestValue>
+void ReadFieldUpdate(TypeList<std::set<LocalType>>, InvokeContext& invoke_context, Input&& input, DestValue&& value)
+{
+    auto data = input.get();
+    value.clear();
+    for (auto item : data) {
