+struct ClientInvokeContext : InvokeContext
+{
+    ThreadContext& thread_context;
+    ClientInvokeContext(Connection& connection, ThreadContext& thread_context)
+        : InvokeContext{connection}, thread_context{thread_context}
+    {
+    }
+};
