+        return boost::get<CKeyID>(dest);
+    }
+    if (boost::get<WitnessV0KeyHash>(&dest)) {
+        return CKeyID(boost::get<WitnessV0KeyHash>(dest));
+    }
+    if (boost::get<CScriptID>(&dest)) {
+        CScript script;
+        CTxDestination inner_dest;
+        if (GetCScript(boost::get<CScriptID>(dest), script) && ExtractDestination(script, inner_dest)) {
