```cpp
for (const auto& dest : GetAllDestinationsForKey(pubkey)) {
    if (!request.params[1].isNull()) {
        pwallet->SetAddressBook(dest, strLabel, "receive")
    } else if (pwallet->mapAddressBook.count(dest) == 0) {
        pwallet->SetAddressBook(dest, strLabel, "receive");
    }
}
