+void AddrIndex::DB::RemoveKeys(const std::vector<std::pair<std::pair<char, uint64_t>, CDiskTxPos>> &keys_to_remove) {
+    CDBBatch batch(*this);
+    for (const auto& key : keys_to_remove) {
+        batch.Erase(key);
+    }
+    WriteBatch(batch);
+}
