+
+                // Subtract fee from the change output if not subtracting it from recipient outputs
+                CAmount fee_needed = nFeeRet;
                 if (nSubtractFeeFromAmount == 0) {
-                    assert(nChangePosInOut != -1);
-                    std::vector<CTxOut>::iterator change_position = txNew.vout.begin()+nChangePosInOut;
-                    change_position->nValue -= nFeeRet;
-
-                    if (change_position->nValue < 0) {
-                        CAmount orig_fee = nFeeRet;
-                        // Maybe the change value was just enough to cover the fee if we did not have change in this transaction
-                        // Let's try dropping the change, recalculating the fee, and comparing the new fee to change_and_Fee
-                        nChangePosInOut = -1;
-                        txNew.vout.erase(change_position);
-
-                        tx_sizes = CalculateMaximumSignedTxSize(CTransaction(txNew), this, coin_control.fAllowWatchOnly);
-                        nBytes = tx_sizes.first;
-                        nFeeRet = coin_selection_params.m_effective_feerate.GetFee(nBytes);
-                        if (nFeeRet <= change_and_fee) {
-                            // Dropping the change value puts us exactly at the value, so we can exit now
-                            break;
-                        }
-
-                        // The change value was not enough to cover the fees, even after dropping the change
-                        // We set nFeeRet back to its original value (the one that includes the change output) and try selecting again
-                        assert(change_and_fee < nFeeRet);
-                        nFeeRet = orig_fee;
-                        continue;
-                    }
-
-                    // The change value was enough to cover the fees. We may want to drop it to fees if it is too small,
-                    // or try selecting again if we don't meet the minimum change requirement
-
-                    // We want to drop the change to fees if:
-                    // 1. The change output would be dust
-                    // 2. The change is within the (almost) exact match window, i.e. it is less than or equal to the cost of the change output (cost_of_change)
-                    if (IsDust(*change_position, coin_selection_params.m_discard_feerate) || change_position->nValue <= coin_selection_params.m_cost_of_change)
-                    {
-                        nFeeRet += change_position->nValue;
-                        nChangePosInOut = -1;
-                        txNew.vout.erase(change_position);
-                    }
-                    break; // Fee requirements were met
+                    change_position->nValue -= fee_needed;
                 }
+
+                // We want to drop the change output if:
+                // 1. The change output would be dust
+                // 2. The change is within the (almost) exact match window, i.e. it is less than or equal to the cost of the change output (cost_of_change)
+                CAmount change_amount = change_position->nValue;
+                if (IsDust(*change_position, coin_selection_params.m_discard_feerate) || change_amount <= coin_selection_params.m_cost_of_change) {
+                    nChangePosInOut = -1;
+                    txNew.vout.erase(change_position);
+                    change_amount = 0;
+
+                    // Because we have dropped this change, our tx size and required fee will be different. So let's recalculate those.
+                    tx_sizes = CalculateMaximumSignedTxSize(CTransaction(txNew), this, coin_control.fAllowWatchOnly);
+                    nBytes = tx_sizes.first;
+                    fee_needed = coin_selection_params.m_effective_feerate.GetFee(nBytes);
+                }
+
+                // Break if fee is covered and no need to loop or subtract from recipients.
+                if (fee_needed <= change_and_fee - change_amount) {
+                    nFeeRet = change_and_fee - change_amount;
+                    break;
+                }
+
