+void CompareVec(T v1, T v2)
+{
+    BOOST_CHECK_EQUAL(v1.size(), v2.size());
+    for (size_t i = 0; i < v1.size(); ++i) {
+        BOOST_CHECK_EQUAL(v1[i], v2[i]);
+    }
