+{
+    LOCK(cs_db);
+    assert(mapFileUseCount.count(strFile) == 0);
+
+    try {
+        Db db(&dbenv, 0);
+        int result = db.verify(strFile.c_str(), NULL, NULL, 0);
+        if (result == 0)
+            return VERIFY_OK;
+    }
