+    if (ret != SQLITE_ROW) {
+        sqlite3_finalize(app_id_stmt);
+        sqlite3_close(db);
+        error = strprintf(_("SQLiteDatabase: Failed to fetch the application id: %s"), sqlite3_errstr(ret));
+        return false;
+    }
