+void TxRequestTracker::SetTimePoint(std::chrono::microseconds now)
+{
+    // Iterate over all CANDIDATE_NEW and REQUESTED from old to new, as long as they're in the past,
+    // and convert them to CANDIDATE_OTHER and COMPLETED respectively.
+    while (!m_index.empty()) {
+        auto it = m_index.get<ByTime>().begin();
+        if (it->GetState() == State::CANDIDATE_NEW && it->m_time <= now) {
+            PromoteCandidateNew(m_index.project<ByTxid>(it));
+        } else if (it->GetState() == State::REQUESTED && it->m_time <= now) {
+            MakeCompleted(m_index.project<ByTxid>(it));
+        } else {
+            break;
+        }
+    }
+
+    while (!m_index.empty()) {
+        // If time went backwards, we may need to demote CANDIDATE_BEST and CANDIDATE_OTHER entries back
+        // to CANDIDATE_NEW. This is an unusual edge case, and unlikely to matter in production. However,
+        // it makes it much easier to specify and test TxRequestTracker's behaviour.
+        auto it = std::prev(m_index.get<ByTime>().end());
+        if (it->IsSelectable() && it->m_time > now) {
+            ChangeAndReselect(m_index.project<ByTxid>(it), State::CANDIDATE_NEW);
+        } else {
+            break;
+        }
+    }
+}
