+void TxRequestTracker::AlreadyHaveTx(const uint256& txid)
+{
+    auto it = m_index.get<ByTxid>().lower_bound(EntryTxid{txid, State::CANDIDATE_NEW, 0});
+    while (it != m_index.get<ByTxid>().end() && it->m_txid == txid) {
+        it = Erase<ByTxid>(it);
+    }
+}
