+    assert(it != m_index.get<ByPeer>().end());
+    assert(it->GetState() == State::CANDIDATE_BEST);
+    Modify<ByPeer>(it, [now,this](Entry& entry) {
+        entry.SetState(State::REQUESTED);
+        entry.m_time = now + m_timeout;
+    });
