     {
-        Check();
+        LOCK(cs);
+        CheckWithLockHeld();
         std::vector<CAddress> vAddr;
-        {
-            LOCK(cs);
-            GetAddr_(vAddr, max_addresses, max_pct);
-        }
-        Check();
