void ProtectEvictionCandidatesByRatio(std::vector<NodeEvictionCandidate>& candidates)
{
    // Treat localhost connections as a separate network.
    static constexpr Network NET_LOCALHOST{NET_MAX};

    const std::array<Network, 3> networks{NET_LOCALHOST, NET_I2P, NET_ONION};

    // Protect the half of the remaining nodes which have been connected the longest.
    // This replicates the non-eviction implicit behavior, and precludes attacks that start later.
    // To favorise the diversity of our peer connections, reserve up to half of
    // these protected spots for onion, localhost and I2P peers, even if they're
    // not longest uptime overall. This helps protect these higher-latency peers
    // that tend to be otherwise disadvantaged under our eviction criteria.
    const size_t total_to_protect{candidates.size() / 2};

    // Protect up to 25% of `candidates` due to network. We may protect less if
    // there are no candidates that belong to `networks`.

    const size_t max_protect_due_to_network{total_to_protect / 2};
    size_t num_protected{0};

    while (num_protected < max_protect_due_to_network) {
        const size_t remaining_to_protect{max_protect_due_to_network - num_protected};
        const size_t protect_per_network{std::max(remaining_to_protect / networks.size(), 1UL)};
        bool protected_at_least_one{false};

        for (const Network net : networks) {
            const size_t before = candidates.size();
            EraseLastKElements(candidates,
                               CompareNodeNetworkTime(net, net == NET_LOCALHOST),
                               protect_per_network,
                               [&net](const NodeEvictionCandidate& c) {
                                   return net == NET_LOCALHOST ? c.m_is_local : c.m_network == net;
                               });
            const size_t after = candidates.size();
            if (after < before) {
                protected_at_least_one = true;
                num_protected += before - after;
                if (num_protected >= max_protect_due_to_network) {
                    break;
                }
            }
        }

        if (!protected_at_least_one) {
            break;
        }
    }

    // Protect another 25% to 50% of `candidates` due to uptime/longevity. For example, if
    // 25% were protected due to network then we will protect 25% due to uptime, if
    // 20% were protected due to network then we will protect 30% due to uptime.
    const size_t remaining{total_to_protect - num_protected};
    EraseLastKElements(candidates, ReverseCompareNodeTimeConnected, remaining);
}
```

