index 3eca506f08..c0b28acd47 100644
--- a/src/wallet/wallet.cpp
+++ b/src/wallet/wallet.cpp
@@ -1911,9 +1911,11 @@ OutputType CWallet::TransactionChangeType(const std::optional<OutputType>& chang
         if (is_wit && witnessversion >= 1 && GetScriptPubKeyMan(OutputType::BECH32M, true)) {
             // If this is a segwit v1+ address and this wallet supports bech32m addresses, use bech32m
             return OutputType::BECH32M;
-        } else if (is_wit) {
-            // Otherwise if this is witness, use bech32
+        } else if (is_wit && GetScriptPubKeyMan(OutputType::BECH32, true)) {
+            // Otherwise if this is witness, use bech32 if we have a descriptor:
             return OutputType::BECH32;
+        } else {
+            return m_default_address_type;
         }
     }
