+#ifndef BITCOIN_SUPPORT_ALLOCATORS_NODE_ALLOCATOR_H
+#define BITCOIN_SUPPORT_ALLOCATORS_NODE_ALLOCATOR_H
+
+#include <memusage.h>
+
+#include <cstddef>
+#include <new>
+#include <type_traits>
+#include <unordered_map>
+#include <utility>
