+        for (auto* block : m_allocated_blocks) {
+            ::operator delete(block);
+        }
+        m_free_chunks = nullptr;
+        m_allocated_blocks.clear();
+        m_chunk_size_bytes = 0U;
+        m_untouched_memory_iterator = nullptr;
+        m_untouched_memory_end = nullptr;
+    }
+
