+++ b/src/support/allocators/node_allocator.h
@@ -385,7 +385,8 @@ template <typename Key, typename Value, typename Hash = std::hash<Key>, typename
 class UnorderedMapFactory
 {
 public:
-    static constexpr size_t ALLOCATION_SIZE_BYTES = memusage::NodeSize<std::unordered_map<Key, Value, Hash, Equals>>::Value();
+    static constexpr size_t ALLOCATION_SIZE_BYTES = detail::RequiredAllocationSizeBytes<typename
+                                                    memusage::NodeSize<std::unordered_map<Key, Value, Hash, Equals>>::NodeType>();
     using MemoryResourceType = MemoryResource<ALLOCATION_SIZE_BYTES>;
     using AllocatorType = Allocator<std::pair<const Key, Value>, ALLOCATION_SIZE_BYTES>;
     using ContainerType = std::unordered_map<Key, Value, Hash, Equals, AllocatorType>;
diff --git a/src/memusage.h b/src/memusage.h
index fd781f77bf5..af1f33963d6 100644
--- a/src/memusage.h
+++ b/src/memusage.h
@@ -163,35 +163,38 @@ struct NodeSize {
