+        for (int i = 0; i < 100; ++i) {
+            map_a[i] = i;
+        }
+
+        {
+            auto map_b = Factory::CreateContainer(&mr_b);
+            map_b[123] = 321;
+            BOOST_CHECK(map_a.get_allocator() != map_b.get_allocator());
