+    BOOST_CHECK_EQUAL(num_free_allocations,                                          \
+                      node_allocator::MemoryResourceTester::NumFreeAllocations(mr)); \
+    BOOST_CHECK_EQUAL(num_pools, node_allocator::MemoryResourceTester::NumPools(mr));
+
+#define CHECK_IN_RANGE(what, lowerInclusive, upperInclusive) \
+    BOOST_TEST(what >= lowerInclusive);                      \
+    BOOST_TEST(what <= upperInclusive);
+
+BOOST_AUTO_TEST_CASE(too_small)
+{
+    node_allocator::MemoryResource<sizeof(void*)> mr{};
+    void* ptr{mr.Allocate<char>(1)};
+    BOOST_CHECK(ptr != nullptr);
+
+    // mr is used
+    CHECK_MEMORY_RESOURCE(mr, 0, 1);
+    mr.Deallocate<char>(ptr, 1);
+    CHECK_MEMORY_RESOURCE(mr, 1, 1);
+
+    // void* works too, use freelist
+    ptr = mr.Allocate<void*>(1);
+    BOOST_CHECK(ptr != nullptr);
+    CHECK_MEMORY_RESOURCE(mr, 0, 1);
