+    // a is destroyed, still all allocations should stay roughly the same.
+    BOOST_CHECK(node_allocator::MemoryResourceTester::NumFreeAllocations(mr) <=
+                num_free_allocations + 5);
+
+    m = Factory::CreateContainer(&mr);
+
