+void KeyManager::GenerateAndSetHDKey()
+{
+    LOCK(cs_keyman);
+    CKey key;
+    key.MakeNewKey(true);
+    CPubKey seed_pub = key.GetPubKey();
+    assert(key.VerifyPubKey(seed_pub));
