+
+    if (undoTX == nullptr) {
+        return false;
+    }
+
+    const Coin& prev_coin = undoTX->vprevout[0];
+
+    const CTxIn& txin = tx->vin.at(0);
+
+    return ExtractPubkeyFromInput(prev_coin, txin, firstInputPubKey);
