+        return false;
+    } else if (whichType == TxoutType::NONSTANDARD || whichType == TxoutType::MULTISIG || whichType == TxoutType::WITNESS_UNKNOWN ) {
+        return false;
+    } else {
+        privKey = key.second;
+    }
+
+    return true;
+}
