+
+            const auto& sender_seckey{sender_secret_keys.at(i)};
+
+            unsigned char seckey_i[32];
+            memcpy(seckey_i, sender_seckey.begin(), 32);
+            assert(memcmp(seckey_i, sender_seckey.begin(), 32) == 0);
+
+            if (sender_seckey.GetPubKey()[0] == 3) {
