+
+    if (auto* conf = std::get_if<TxStateConfirmed>(&state)) {
+        for (const CTxIn& txin : tx.vin) {
+            std::pair<TxSpends::const_iterator, TxSpends::const_iterator> range = mapTxSpends.equal_range(txin.prevout);
