+/**Comparator for sorting m_txns which contains reference wrappers.*/
+struct CompareEntry {
+    template<typename T>
+    bool operator()(const std::reference_wrapper<T> a, const std::reference_wrapper<T> b) const
+    {
+        return a.get() < b.get();
+    }
