+void AncestorPackage::AddFeeAndVsize(const uint256& txid, CAmount fee, int64_t vsize)
+{
+    if (m_txid_to_entry.count(txid) == 0) return;
+    m_txid_to_entry.at(txid).fee = fee;
+    m_txid_to_entry.at(txid).vsize = vsize;
