+        if (curr_try >= TOTAL_TRIES || next_op == operations::done) {
+            result.SetAlgoCompleted(next_op == operations::done); // True if done, false if curr_try hit TOTAL_TRIES
+            // Exhausted search space or reached limit
+            break;
