+            std::set<Txid> txid_self{tx->GetHash()};
+            Assume(v3_ancestor_sets.emplace(tx->GetHash(), txid_self).second);
+            Assume(v3_descendant_sets.emplace(tx->GetHash(), txid_self).second);
+        } else {
