+    { "gethdkeys", 0, "options" },
     { "gethdkeys", 0, "active_only" },
-    { "gethdkeys", 1, "private" },
+    { "gethdkeys", 0, "private" },
     { "createwalletdescriptor", 1, "internal" },
     // Echo with conversion (For testing only)
