+
+CMoney CMoney::FromString(const char* pszIn)
+{
+    std::string strWhole;
+    CMoney nUnits = 0;
+    const char* p = pszIn;
+    while (isspace(*p))
+        p++;
+    for (; *p; p++)
+    {
+        if (*p == '.')
+        {
+            p++;
+            int64_t nMult = CENT*10;
+            while (isdigit(*p) && (nMult > 0))
+            {
+                nUnits += nMult * (*p++ - '0');
+                nMult /= 10;
+            }
+            break;
+        }
+        if (isspace(*p))
+            break;
+        if (!isdigit(*p))
+            throw invalid_money_format(pszIn);
+        strWhole.insert(strWhole.end(), *p);
+    }
+    for (; *p; p++)
+        if (!isspace(*p))
+            throw invalid_money_format(pszIn);
+    if (strWhole.size() > 10) // guard against 63 bit overflow
+        throw money_overflow(pszIn);
+    if (nUnits < 0 || nUnits > COIN)
+        throw money_overflow(pszIn);
+    int64_t nWhole = atoi64(strWhole);
+    CMoney nValue = nWhole*COIN + nUnits;
+
+    return nValue;
+}
+
+std::string CMoney::ToString(bool fPlus) const
+{
+    static const int64_t COIN_I64 = COIN;
+    // Note: not using straight sprintf here because we do NOT want
+    // localized number formatting.
+    int64_t n_abs = (n > 0 ? n : -n);
+    int64_t quotient = n_abs / COIN_I64;
+    int64_t remainder = n_abs % COIN_I64;
+    std::string str = strprintf("%d.%08d", quotient, remainder);
+
+    // Right-trim excess zeros before the decimal point:
+    int nTrim = 0;
+    for (int i = str.size()-1; (str[i] == '0' && isdigit(str[i-2])); --i)
+        ++nTrim;
+    if (nTrim)
+        str.erase(str.size()-nTrim, nTrim);
+
+    if (n < 0)
+        str.insert((unsigned int)0, 1, '-');
+    else if (fPlus && n > 0)
+        str.insert((unsigned int)0, 1, '+');
+    return str;
+}
+
+const CMoney operator/(const CMoney& a, const CMoney& b)

