+        else if (fragment == "c")
+            return false;
+        else
+        {
+            CExtKey childKey;
+            int32_t nIndex;
+            if (!ParseInt32(fragment,&nIndex))
+                return false;
+            parentKey.Derive(childKey, (harden ? 0x80000000 : 0)+nIndex);
+            parentKey = childKey;
+        }
+    }
+    extKeyOut = parentKey;
+    return true;
+}
