+/** Convert 8 sliced integers into 16 bytes of data */
+void SaveBytes(unsigned char* data, const uint16_t *slice) {
+    for (int c = 0; c < 4; c++) {
+        for (int r = 0; r < 4; r++) {
+            uint8_t v = 0;
+            for (int b = 0; b < 8; b++) {
+                v |= ((slice[b] >> (r * 4 + c)) & 1) << b;
+            }
+            *(data++) = v;
