@@ -1756,6 +1757,7 @@ UniValue listsinceblock(const JSONRPCRequest& request)

     UniValue ret(UniValue::VOBJ);
     ret.push_back(Pair("transactions", transactions));
+    ret.push_back(Pair("reorged", reorged));

